function enumerate(arr) {
  var obj = {};
  var i = 0;
  for(var i = 0, len = arr.length; i < len; i++) {
    obj[arr[i]] = i;
  }
  return obj;
}

const STATUS = enumerate([
  'OK',
  'PENDING',
  'TIMEOUT',
  'SERVER_FAILURE',
  'UNKNOWN_ERROR'
]);
const DATA_TYPE = enumerate([
  'JSON',
  'TEXT'
]);
const ERR_MSG = {
  BAD_REQUEST: {
    status: 400,
    msg: 'Missing username or password'
  },
  TIMEOUT: {
    status: 504,
    msg: 'Remote server timeout'
  },
  SERVER_FAILURE: {
    status: 502,
    msg: 'Remote server error'
  }
}
// {
//   OK: 0,
//   SERVER_FAILURE: 1,
//   ...
// }

module.exports = {
  STATUS: STATUS,
  DATA_TYPE: DATA_TYPE,
  ERR_MSG: ERR_MSG
}